import React, { useEffect, useState } from 'react';

function User() {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('https://646d4d739c677e232189e51a.mockapi.io/user');
      const jsonData = await response.json();
      setUsers(jsonData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleCreate = async () => {
    try {
      const response = await fetch('https://646d4d739c677e232189e51a.mockapi.io/user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email }),
      });
      const newUser = await response.json();
      setUsers([...users, newUser]);
      setName('');
      setEmail('');
    } catch (error) {
      console.error('Error creating user:', error);
    }
  };

  const handleUpdate = async (userId) => {
    try {
      const response = await fetch(`https://646d4d739c677e232189e51a.mockapi.io/user/${userId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email }),
      });
      const updatedUser = await response.json();
      const updatedUsers = users.map((user) => (user.id === userId ? updatedUser : user));
      setUsers(updatedUsers);
      setName('');
      setEmail('');
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <>
      <section>
        <h2>Create User</h2>
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <button onClick={handleCreate}>Create</button>
      </section>

      <section>
        <h2>User List</h2>
        {users.length > 0 ? (
          <ul>
            {users.map((user) => (
              <li key={user.id}>
                <strong>Name:</strong> {user.name}<br />
                <strong>Email:</strong> {user.email}<br />
                <button onClick={() => handleUpdate(user.id)}>Update</button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No users found.</p>
        )}
      </section>
    </>
  );
}


export default User;
